<?php
  require_once "config/db.php";

  $sqlPosts = $conn->prepare("SELECT post.post_id, post.title, post.content, post.img_title, post.user_id, post.province_id, post.amphure_id, post.district_id, users.user_id, users.firstname, users.lastname, provinces.name_th AS province_name ,amphures.name_th AS amphure_name ,districts.name_th AS district_name, package.package_id, package.post_id, MIN(package.package_price) AS min_price
  FROM post,users,provinces,amphures,districts,package
  WHERE post.user_id=users.user_id AND post.province_id=provinces.id AND post.amphure_id=amphures.id AND post.district_id=districts.id AND post.post_id = package.post_id GROUP BY package.post_id ORDER BY (SELECT COUNT(orders.order_end) AS finish FROM orders WHERE orders.post_id = post.post_id) DESC;");
  $sqlPosts->execute();
  $sqlSelect = $sqlPosts->fetchAll(PDO::FETCH_ASSOC);
?>
